package com.example.pashko00710.bashimmvp.core;import android.support.annotation.NonNull;import com.example.pashko00710.bashimmvp.core.rest.BashimApi;import com.example.pashko00710.bashimmvp.core.rest.models.Quote;import com.example.pashko00710.bashimmvp.core.storage.DbHelper;import com.example.pashko00710.bashimmvp.core.storage.entities.QuoteEntity;import com.example.pashko00710.bashimmvp.utils.ConstantsManager;import java.util.List;import javax.inject.Inject;import javax.inject.Singleton;import rx.Observable;@Singletonpublic class DataManager {    private DbHelper dbHelper;    private BashimApi bashimApi;    @Inject    public DataManager(@NonNull DbHelper dbHelper, @NonNull BashimApi bashimApi) {        this.dbHelper = dbHelper;        this.bashimApi = bashimApi;    }    @NonNull    public Observable<Quote> loadQuotes() {        return bashimApi.loadQuotes(ConstantsManager.SITE_PARAM, ConstantsManager.NAME_PARAM,                100).concatMap(quotes -> dbHelper.insertQuotes(quotes));    }    @NonNull    public Observable<List<QuoteEntity>> getQuotes() {        return dbHelper.selectQuotes();    }}