package com.example.pashko00710.bashimmvp.core.rest;import com.example.pashko00710.bashimmvp.core.rest.models.Quote;import java.util.List;import retrofit2.http.GET;import retrofit2.http.Query;import rx.Observable;public interface BashimApi {    @GET("get")    Observable<List<Quote>> loadQuotes(@Query("site") String site,                                       @Query("name") String name,                                       @Query("num") int number);}