package com.example.pashko00710.bashimmvp.core.storage;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import android.support.annotation.NonNull;import com.example.pashko00710.bashimmvp.core.storage.entities.QuoteEntity;import com.example.pashko00710.bashimmvp.injection.ApplicationContext;import com.example.pashko00710.bashimmvp.utils.ConstantsManager;import javax.inject.Inject;public class DbOpenHelper extends SQLiteOpenHelper {    @Inject    public DbOpenHelper(@NonNull @ApplicationContext Context context) {        super(context, ConstantsManager.DATABASE_NAME, null, ConstantsManager.DATABASE_VERSION);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.beginTransaction();        try {            db.execSQL(QuoteEntity.CREATE_TABLE);            db.setTransactionSuccessful();        } finally {            db.endTransaction();        }    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        db.execSQL("DROP TABLE IF EXISTS " + QuoteEntity.TABLE_NAME);        onCreate(db);    }}