package com.example.pashko00710.bashimmvp.injection.components;import android.app.Application;import android.content.Context;import com.example.pashko00710.bashimmvp.core.DataManager;import com.example.pashko00710.bashimmvp.core.rest.RestModule;import com.example.pashko00710.bashimmvp.core.services.LoadQuotesService;import com.example.pashko00710.bashimmvp.core.storage.DbHelper;import com.example.pashko00710.bashimmvp.injection.ApplicationContext;import com.example.pashko00710.bashimmvp.injection.modules.ActivityModule;import com.example.pashko00710.bashimmvp.injection.modules.ApplicationModule;import javax.inject.Singleton;import dagger.Component;@Singleton@Component(modules = {ApplicationModule.class, RestModule.class})public interface ApplicationComponent {    @ApplicationContext    Context context();    Application application();    ActivityComponent addActivityComponent(ActivityModule activityModule);    DbHelper providesDbHelper();    DataManager providesDataManager();    void inject(LoadQuotesService loadQuotesService);}